// Install the Redis library via Composer\n// composer require predis/predis\n\n// Configure Redis in your CI application\n$config['redis'] = array(\n    'host' => 'localhost',\n    'port' => 6379,\n    'database' => 0,\n);\n\n// Create a Redis instance in your controller\n$redis = new Predis\Client($this->config->item('redis'));\n\n// Cache a query result for 1 hour\n$result = $this->db->query('SELECT * FROM my_table');\n$redis->set('my_table_result', $result->result_array());\n$redis->expire('my_table_result', 3600);