use Goutte\Client;\n\n$client = new Client();\n\n// Navigate to the main page\n$crawler = $client->request('GET', 'http://www.somesite.com/');\n\n// Follow a link that contains the text 'download this'\n$link = $crawler->selectLink('download this')->link();\n$crawler = $client->click($link);\n\n// Submit a POST form to log into the site\n$form = $crawler->selectButton('Login')->form();\n$form['_username'] = 'mungo';\n$form['_password'] = 'lost-and-alone';\n$crawler = $client->submit($form);\n\n// Save the results as a file\n$file = $client->getResponse()->getContent();\nfile_put_contents('somefile.zip', $file);