// Create the tables\n$mysqli = new mysqli("localhost", "username", "password", "database");\n\n$mysqli->query("CREATE TABLE vote (\n  id INT PRIMARY KEY AUTO_INCREMENT,\n  user_id INT,\n  date TIMESTAMP DEFAULT CURRENT_TIMESTAMP\n)");\n\n$mysqli->query("CREATE TABLE vote_details (\n  vote_id INT,\n  product_id INT,\n  vote TINYINT,\n  PRIMARY KEY (vote_id, product_id),\n  FOREIGN KEY (vote_id) REFERENCES vote(id)\n)");\n\n// Insert data\n$mysqli->query("INSERT INTO vote (user_id) VALUES (1)");\n$vote_id = $mysqli->insert_id;\n\n$mysqli->query("INSERT INTO vote_details (vote_id, product_id, vote) VALUES ($vote_id, 1, 5)");\n$mysqli->query("INSERT INTO vote_details (vote_id, product_id, vote) VALUES ($vote_id, 3, 4)");