function encryptArray($array, $key) {\n  $json = json_encode($array);\n  $encrypted = openssl_encrypt($json, 'AES-128-CBC', $key, 0, substr($key, 0, 16));\n  return base64_encode($encrypted);\n}\n\nfunction decryptArray($encrypted, $key) {\n  $decrypted = openssl_decrypt(base64_decode($encrypted), 'AES-128-CBC', $key, 0, substr($key, 0, 16));\n  return json_decode($decrypted, true);\n}\n\n// Example usage:\n$array = array('foo' => 'bar', 'baz' => 'qux');\n$key = 'your_secret_key_here';\n\n$encrypted = encryptArray($array, $key);\necho "Encrypted: $encrypted\n";\n\n$decrypted = decryptArray($encrypted, $key);\nprint_r($decrypted);