// Start the process\nfunction startProcess() {\n  $token = uniqid();\n  // start the process and store the token in a database or cache layer\n  // ...\n  return $token;\n}\n\n// Get the progress\nfunction getProgress($token) {\n  // retrieve the progress information from the database or cache layer\n  // ...\n  return $progress;\n}\n\n// Handle the AJAX requests\nif ($_SERVER['REQUEST_METHOD'] == 'POST' && $_POST['action'] == 'start-process') {\n  $token = startProcess();\n  echo $token;\n  exit;\n}\n\nif ($_SERVER['REQUEST_METHOD'] == 'GET' && $_GET['action'] == 'get-progress') {\n  $token = $_GET['token'];\n  $progress = getProgress($token);\n  echo $progress;\n  exit;\n}