$acl = new Zend_Acl();\n\n// Create resource structure\n$acl->addResource('root');\n$acl->addResource('post', 'root');\n$acl->addResource('gallery', 'root');\n$acl->addResource('announcement', 'root');\n$acl->addResource('announcement_gallery', 'announcement');\n\n// Grant permissions on resource types\n$acl->allow('user1', 'post', 'edit');\n$acl->allow('guest', 'gallery', 'read');\n\n// Grant permissions on specific resource instances\n$acl->allow('user2', 'post:5', 'edit');\n\n// Check permissions in a module\nif ($acl->isAllowed('user1', 'post', 'edit')) {\n    // User1 can edit posts\n} elseif ($acl->isAllowed('user1', 'post:5', 'edit')) {\n    // User1 can edit post #5\n} else {\n    // Deny access\n}