defined('_JEXEC') or die;\n\nclass PlgContentMulticolumn extends JPlugin\n{\n    public function onContentPrepare($context, &$article, &$params, $page = 0)\n    {\n        // Check if the article is being displayed in a supported context\n        if ($context !== 'com_content.article') {\n            return;\n        }\n\n        // Load phpQuery library\n        require_once JPATH_LIBRARIES . '/phpquery/phpQuery.php';\n\n        // Split the article content into columns\n        $delimiter = '<!-- column break -->';\n        $columns = preg_split('/' . preg_quote($delimiter, '/') . '/', $article->text);\n\n        // Create a new HTML structure for the columns\n        $html = array();\n        foreach ($columns as $column) {\n            $html[] = '<div class="col-md-4">' . $column . '</div>';\n        }\n\n        // Replace the original article content with the new HTML structure\n        $article->text = implode('', $html);\n    }\n}