function displayTable($table, $order, $sort) {\n    $query = "SELECT v.*, ve.venue_name \n              FROM $table v \n              LEFT JOIN venues ve ON v.venue_id = ve.id \n              ORDER BY $order $sort";\n    $result = mysql_query($query);\n\n    if($_POST) { ?>\n        <table id="box-table-a">\n        <tr>\n            <th>Name</th>\n            <?php if($table == 'maillist') { ?>\n                <th>Email</th>\n            <?php } ?>\n            <?php if($table == 'volunteers_2008' || $table == 'volunteers_2009') { ?>\n                <th>Comments</th>\n            <?php } ?>\n            <?php if($table == 'volunteers_2009') { ?>\n                <th>Interests</th>\n                <th>Venue</th>\n            <?php } ?>\n            <th>Edit</th>\n        </tr>\n        <tr>\n        <?php\n        while($row = mysql_fetch_array($result))\n        {\n            $i = 0;\n            while($i <=0)\n            {\n                print '<td>'.$row['fname'].' '.$row['lname'].'</td>';\n                if($table == 'maillist') {\n                    print '<td><a href="mailto:'.strtolower($row['email']).'">'.strtolower($row['email']).'</a></td>';\n                }\n                if($table == 'volunteers_2008' || $table == 'volunteers_2009') {\n                    print '<td><small>'.substr($row['comments'], 0, 32).'</small></td>';\n                }\n                if($table == 'volunteers_2009') {\n                    print '<td><small>1) '.$row['choice1'].'<br>2) '.$row['choice2'].'<br>3) '.$row['choice3'].'</small></td>';\n                    if($row['venue_name'] != '') {\n                        print '<td>'.$row['venue_name'].'</td>';\n                    } else {\n                        print '<td>No Venue Assigned</td>';\n                    }\n                }\n                ?>\n                <td><a href="?mode=upd&id=<?= $row['id'] ?>&table=<?= $table ?>">Upd</a> / <a href="?mode=del&id=<?= $row['id'] ?>&table=<?= $table ?>" onclick="return confirm('Are you sure you want to delete?')">Del</a></td>\n                <?php\n                $i++;\n            }\n            print '</tr>';\n        }\n        print '</table>';\n    }\n}