// db2_api.php (on CentOS server)\n\nrequire_once 'db2_connect.php'; // DB2 connection settings\n\nfunction get_data($query) {\n    $conn = db2_connect(); // Establish DB2 connection\n    $stmt = db2_prepare($conn, $query); // Prepare SQL statement\n    db2_execute($stmt); // Execute SQL statement\n    $result = db2_fetch_assoc($stmt); // Fetch result as associative array\n    db2_close($conn); // Close DB2 connection\n    return json_encode($result); // Return JSON representation of data\n}\n\n// API endpoint for retrieving data\n$app->get('/data', function() use ($app) {\n    $query = 'SELECT * FROM your_table'; // Replace with your SQL query\n    $data = get_data($query);\n    $app->response()->header('Content-Type', 'application/json');\n    echo $data;\n});