if (isset($_GET['user_id'])) {\n    $content = 'user_id';\n} elseif (isset($_GET['content'])) {\n    $content = $_GET['content'];\n} else {\n    $content = 'default';\n}\n\nswitch ($content) {\n    case 'news':\n        include_once('news.php');\n        break;\n    case 'user_id':\n        include_once('profile.php');\n        break;\n    default:\n        include_once('main.php');\n        break;\n}