function validateWebsiteName($websiteName) {\n    $pattern = '/^(http:\/\/|https:\/\/)?([a-z0-9-]+\.)+[a-z]{2,}(\/)?$/i';\n    return preg_match($pattern, $websiteName) === 1;\n}\n\n// Test cases\n$websiteNames = [\n    'somewebsite.com',\n    'http://somewebsite.com',\n    'http://www.somewebsite.com',\n    'somewebsite.com/',\n    'subdomain.somewebsite.com',\n];\n\nforeach ($websiteNames as $websiteName) {\n    echo $websiteName . ': ' . (validateWebsiteName($websiteName) ? 'Valid' : 'Invalid') . "\n";\n}