use Psr\SimpleCache\CacheInterface;\n\nclass Autoloader\n{\n    private $cache;\n    private $namespacePrefixes = [];\n\n    public function __construct(CacheInterface $cache)\n    {\n        $this->cache = $cache;\n    }\n\n    public function addNamespacePrefix(string $prefix, string $directory)\n    {\n        $this->namespacePrefixes[$prefix] = $directory;\n    }\n\n    public function autoload(string $class)\n    {\n        $cacheKey = 'autoload_' . $class;\n        if ($this->cache->has($cacheKey)) {\n            return $this->cache->get($cacheKey);\n        }\n\n        $namespacePrefix = substr($class, 0, strpos($class, '\\'));\n        if (isset($this->namespacePrefixes[$namespacePrefix])) {\n            $directory = $this->namespacePrefixes[$namespacePrefix];\n            $filePath = $directory . DIRECTORY_SEPARATOR . substr($class, strlen($namespacePrefix) + 1) . '.php';\n            if (file_exists($filePath)) {\n                $this->cache->set($cacheKey, $filePath);\n                return $filePath;\n            }\n        }\n\n        return null;\n    }\n}