function getNgrams($text, $n, $level = 'word') {\n    $ngrams = [];\n    $tokens = [];\n\n    if ($level === 'word') {\n        $tokens = explode(' ', $text);\n    } elseif ($level === 'character') {\n        $tokens = str_split($text);\n    }\n\n    for ($i = 0; $i < count($tokens) - $n + 1; $i++) {\n        $ngram = implode(' ', array_slice($tokens, $i, $n));\n        $ngrams[] = $ngram;\n    }\n\n    return $ngrams;\n}\n\n// Example usage:\n$text = "I live in NY";\n$n = 2;\n$level = 'word';\n\n$ngrams = getNgrams($text, $n, $level);\nprint_r($ngrams);