public function testFileUpload()\n{\n    $filename = 'example.txt';\n    $fileContent = 'Hello, World!';\n    $fileSize = strlen($fileContent);\n\n    $this->getRequest()->setMethod('POST');\n    $_FILES = array(\n        'file' => array(\n            'name' => $filename,\n            'type' => 'text/plain',\n            'size' => $fileSize,\n            'tmp_name' => '/tmp/' . $filename,\n            'error' => UPLOAD_ERR_OK,\n        ),\n    );\n\n    // Set the file content\n    file_put_contents($_FILES['file']['tmp_name'], $fileContent);\n\n    $this->dispatch('/some/url');\n\n    // Assert that the file was uploaded successfully\n    $this->assertResponseCode(200);\n    $this->assertFileExists('/path/to/uploaded/file/' . $filename);\n}