$dsn = 'sqlite:example.db';\n$conn = new PDO($dsn);\n\n// Create table\n$conn->exec("CREATE TABLE IF NOT EXISTS test (\n                id INTEGER PRIMARY KEY, \n                name TEXT NOT NULL\n              );");\n\n// Insert data\n$conn->exec("INSERT INTO test (name) VALUES ('John Doe');");\n\n// Select data\n$stmt = $conn->query("SELECT * FROM test");\nforeach ($stmt as $row) {\n    echo $row['name'] . "\n";\n}\n\n$conn = null;