$author_array = array(8, 1, 6);\n$user_results = array(\n    (object) array('ID' => 1, 'user_login' => 'user1'),\n    (object) array('ID' => 6, 'user_login' => 'user6'),\n    (object) array('ID' => 8, 'user_login' => 'user8'),\n);\n\nuasort($user_results, function($a, $b) use ($author_array) {\n    $index_a = array_search($a->ID, $author_array);\n    $index_b = array_search($b->ID, $author_array);\n    return $index_a <=> $index_b;\n});\n\nprint_r($user_results);