$query = "\n    SELECT \n        s.*,\n        i.*\n    FROM \n        scales s\n    LEFT JOIN \n        items i ON s.scaleID = i.scaleID\n    ORDER BY \n        s.scaleID\n";\n\n$result = mysqli_query($db, $query);\n\n$scales = array();\nwhile ($row = mysqli_fetch_assoc($result)) {\n    if (!isset($scales[$row['scaleID']])) {\n        $scales[$row['scaleID']] = array(\n            'scale' => $row,\n            'items' => array()\n        );\n    }\n    $scales[$row['scaleID']]['items'][] = $row;\n}\n\n// Now you can iterate through the scales and items\nforeach ($scales as $scale) {\n    echo "Scale: " . $scale['scale']['scaleName'] . "\n";\n    foreach ($scale['items'] as $item) {\n        echo "  Item: " . $item['itemName'] . "\n";\n    }\n}