<?php\n$dsn = 'mysql:host=localhost;dbname=test';\n$username = 'root';\n$password = '';\n\ntry {\n    $pdo = new PDO($dsn, $username, $password);\n    $pdo->setAttribute(PDO::ATTR_ERRMODE, PDO::ERRMODE_EXCEPTION);\n} catch (PDOException $e) {\n    echo 'Connection failed: ' . $e->getMessage();\n    exit();\n}\n\n$stmt = $pdo->prepare('SELECT * FROM new_users');\n$stmt->execute();\n$users = $stmt->fetchAll(PDO::FETCH_ASSOC);\n\nforeach ($users as $user) {\n    $names = explode(',', $user['Name']);\n    $firstName = trim($names[0]);\n    $lastName = trim($names[1]);\n\n    $stmt = $pdo->prepare('UPDATE new_users SET FirstName = :firstName, LastName = :lastName WHERE PANumber = :pan');\n    $stmt->bindParam(':firstName', $firstName);\n    $stmt->bindParam(':lastName', $lastName);\n    $stmt->bindParam(':pan', $user['PANumber']);\n    $stmt->execute();\n}