// Load the original image\n$img = imagecreatefromjpeg('path/to/image.jpg');\n$img_w = imagesx($img);\n$img_h = imagesy($img);\n\n// Create a new image for the reflection\n$reflection = imagecreatetruecolor($img_w, $img_h);\nimagealphablending($reflection, false);\nimagesavealpha($reflection, true);\n\n// Create a transparent color\n$transparent = imagecolorallocatealpha($reflection, 0, 0, 0, 127);\n\n// Fill the reflection image with transparent color\nimagefill($reflection, 0, 0, $transparent);\n\n// Copy the original image onto the reflection, with alpha transparency\nfor ($i = 0; $i < $img_h; $i++) {\n    imagecopymerge($reflection, $img, 0, $i, 0, $img_h - $i - 1, $img_w, 1, 100 - ($i * (100 / $img_h)));\n}\n\n// Output the reflection image\nheader('Content-Type: image/png');\nimagepng($reflection);\nimagedestroy($reflection);