function sortFileSystemArray(&$array) {\n    // Sort the directories (keys)\n    ksort($array);\n    \n    foreach ($array as &$subArray) {\n        // If the value is an array, it's a subdirectory, so we recurse\n        if (is_array($subArray)) {\n            sortFileSystemArray($subArray);\n        } else {\n            // If the value is not an array, it's a file, so we sort the files\n            sort($array);\n            break; // Break the loop as we've sorted the files\n        }\n    }\n}\n\n// Example usage:\n$fileSystem = [\n    'dir1' => [\n        'dir2' => [\n            'dir3' => [\n                'dir4' => [\n                    'file1.php',\n                    'abc.php'\n                ]\n            ],\n            'file2.php',\n            'abc.php'\n        ]\n    ],\n    'abc' => [\n        'abc' => [\n            'abc' => [\n                'file5.php'\n            ]\n        ]\n    ]\n];\n\nsortFileSystemArray($fileSystem);\n\nprint_r($fileSystem);