// application/configs/Acl.php\n\nclass Application_Config_Acl extends Zend_Acl\n{\n    public function __construct()\n    {\n        // Add roles\n        $this->addRole('normal');\n        $this->addRole('admin', 'normal'); // admin inherits from normal\n\n        // Add resources\n        $this->addResource('admin');\n\n        // Set permissions\n        $this->deny('normal', 'admin');\n        $this->allow('admin', 'admin');\n    }\n}