/**\n * @file\n * Contains \Drupal\my_module\Plugin\views\display\PlainDisplay.\n */\n\nnamespace Drupal\my_module\Plugin\views\display;\n\nuse Drupal\views\Plugin\views\display\DisplayPluginBase;\nuse Drupal\views\ViewExecutable;\n\n/**\n * Defines a display plugin that displays a view without a page template.\n *\n * @ViewsDisplay(\n *   id = "plain",\n *   title = @Translation("Plain display"),\n *   help = @Translation("Displays a view without a page template."),\n *   theme = "views_view_plain",\n *   admin = @Translation("Plain display")\n * )\n */\nclass PlainDisplay extends DisplayPluginBase {\n\n  /**\n   * {@inheritdoc}\n   */\n  public function execute() {\n    $output = [];\n    $rows = $this->view->result;\n    foreach ($rows as $row) {\n      $output[] = $this->view->rowPlugin->render($row);\n    }\n    return $output;\n  }\n\n}