// Example PHP code to connect to a MySQL database\n$servername = "localhost";\n$username = "username";\n$password = "password";\n$dbname = "database";\n\n// Create connection\n$conn = new mysqli($servername, $username, $password, $dbname);\n\n// Check connection\nif ($conn->connect_error) {\n    die("Connection failed: " . $conn->connect_error);\n}\n\n// Query the database\n$result = $conn->query("SELECT * FROM products");\n\n// Close connection\n$conn->close();