function insert_info_requests($uid, $interests) {\n    // First, insert the interests into the jss_users_interests_table\n    insert_interests($uid, $interests);\n\n    // Now, retrieve the corresponding provider IDs from the jss_providers_assignments_table\n    global $db;\n    $providerIds = array();\n    foreach ($interests as $interest) {\n        $result = $db->query("SELECT provider_id FROM jss_providers_assignments_table WHERE subcategories_id = '$interest'");\n        while ($row = $result->fetch_assoc()) {\n            $providerIds[$interest][] = $row['provider_id'];\n        }\n    }\n\n    // Create the secondary insert query for the jss_info_requests_table\n    $q = "INSERT INTO jss_info_requests_table (users_id, provider_id, subcategories_id) VALUES ";\n    foreach ($interests as $interest) {\n        foreach ($providerIds[$interest] as $providerId) {\n            $q .= " ('$uid', '$providerId', '$interest'),";\n        }\n    }\n    // Remove the last comma and execute the query\n    $db->query(substr($q, 0, -1));\n}