class TestClass {\n    private $value;\n\n    public function __construct($value) {\n        $this->value = $value;\n    }\n\n    public function add($value) {\n        $this->value += $value;\n        return $this;\n    }\n\n    public function subtract($value) {\n        $this->value -= $value;\n        return $this;\n    }\n\n    public function result() {\n        return $this->value;\n    }\n}\n\n$value = (new TestClass(5))->add(3)->subtract(2)->add(8)->result();\necho $value; // Outputs: 14