// User.php (DomainModel)\nclass User {\n    private $id;\n    private $name;\n    private $email;\n\n    public function getId() { return $this->id; }\n    public function getName() { return $this->name; }\n    public function getEmail() { return $this->email; }\n}\n\n// UserMapper.php (DataMapper)\nclass UserMapper extends Doctrine\ORM\Mapper {\n    public function find($id) {\n        // Use Doctrine's query API to retrieve a User object\n        $query = $this->createQueryBuilder('u')\n            ->where('u.id = :id')\n            ->setParameter('id', $id)\n            ->getQuery();\n\n        return $query->getResult();\n    }\n}