require_once 'twitteroauth.php';\n\n$consumerKey = 'your_consumer_key';\n$consumerSecret = 'your_consumer_secret';\n\n$twitter = new TwitterOAuth($consumerKey, $consumerSecret);\n\n// Redirect the user to Twitter's authorization URL\n$twitter->redirect('https://api.twitter.com/oauth/authorize', array('oauth_callback' => 'http://your-website.com/callback'));\n\n// Handle the callback from Twitter\nif (isset($_GET['oauth_verifier'])) {\n    $twitter->auth('https://api.twitter.com/oauth/access_token', array('oauth_verifier' => $_GET['oauth_verifier']));\n    $accessToken = $twitter->getAccessToken();\n    // Store the access token securely\n    // ...\n}