$range = $_SERVER['HTTP_RANGE'];\n$size = filesize('large_file.xml');\nif ($range) {\n    list($start, $end) = explode('-', $range);\n    $start = intval($start);\n    $end = intval($end);\n    $length = $end - $start + 1;\n    header('Content-Range: bytes ' . $start . '-' . $end . '/' . $size);\n    header('Content-Length: ' . $length);\n    $fp = fopen('large_file.xml', 'r');\n    fseek($fp, $start);\n    while ($length > 0) {\n        $chunk = fread($fp, min($length, 8192));\n        echo $chunk;\n        $length -= strlen($chunk);\n    }\n    fclose($fp);\n}