public function ValidReg() {\n    if (!empty($_POST['username']) && !empty($_POST['password']) && !empty($_POST['email']) && !empty($_POST['state'])) {\n        $valid = true;\n\n        $mysqli = new mysqli("your_host", "your_username", "your_password", "your_database");\n\n        $username = $_POST['username'];\n        $password = md5($_POST['password']);\n        $email = $_POST['email'];\n        $firstname = $_POST['firstname'];\n        $lastname = $_POST['lastname'];\n        $state = $_POST['state'];\n\n        $stmt = $mysqli->prepare("SELECT * FROM users WHERE Username = ?");\n        $stmt->bind_param("s", $username);\n        $stmt->execute();\n        $stmt->store_result();\n\n        if ($stmt->num_rows > 0) {\n            echo "<div id='shopperlogin1'><p>Sorry, that username is taken.<br /> Please go back and try again.</p></div>";\n        } else {\n            $confirm_code = md5(uniqid(rand()));\n\n            $stmt = $mysqli->prepare("INSERT INTO temp_users (confirm_code, Username, Password, EmailAddress, FirstName, LastName, State) VALUES (?, ?, ?, ?, ?, ?, ?)");\n            $stmt->bind_param("sssssss", $confirm_code, $username, $password, $email, $firstname, $lastname, $state);\n            $result = $stmt->execute();\n\n            if ($result) {\n                // send e-mail to ...\n                $to = $email;\n\n                // Your subject\n                $subject = "Your confirmation link here";\n\n                // From\n                $header = "blahblah@blahbalh.com";\n\n                // Your message\n                $message = "Your Comfirmation link \r\n";\n                $message .= "Click on this link to activate your account \r\n";\n                $message .= "http://www.employeediscounted.com/secret/login.php?passkey=$confirm_code";\n\n                // send email\n                $sentmail = mail($to, $subject, $message, $header);\n            }\n\n            // if not found\n            else {\n                echo "<div id='emailmsg'>Not found your email in our database.</div>";\n            }\n\n            // if your email succesfully sent\n            if ($sentmail) {\n                echo "<div id='emailmsg'>Your Confirmation link Has Been Sent To Your Email Address.</div>";\n            } else {\n                echo "<div id='emailmsg'>Cannot send Confirmation link to your e-mail address.</div>";\n            }\n        }\n    } else {\n        $valid = false;\n    }\n    return $valid;\n}