// Generate a unique token for the user\n$token = bin2hex(random_bytes(16));\n\n// Store the token and last visit timestamp in the database\n/db_query("INSERT INTO user_tokens (user_id, token, last_visit) VALUES (?, ?, ?)", [$userId, $token, time()]);\n\n// Query the news entries and compare timestamps\n$newsEntries = // retrieve news entries from the database\nforeach ($newsEntries as $entry) {\n    if ($entry['published_at'] > /db_query("SELECT last_visit FROM user_tokens WHERE user_id = ? AND token = ?", [$userId, $token])) {\n        // Mark the entry as "new"\n        $entry['is_new'] = true;\n    } else {\n        $entry['is_new'] = false;\n    }\n}