// Set the cookie name and expiration time (e.g., 30 days)\n$cookieName = 'lastVisit';\n$cookieExpire = time() + (30 * 24 * 60 * 60);\n\n// Check if the cookie exists and update the timestamp if necessary\nif (isset($_COOKIE[$cookieName])) {\n    $lastVisit = $_COOKIE[$cookieName];\n} else {\n    $lastVisit = time();\n    setcookie($cookieName, $lastVisit, $cookieExpire);\n}\n\n// Query the news entries and compare timestamps\n$newsEntries = // retrieve news entries from the database\nforeach ($newsEntries as $entry) {\n    if ($entry['published_at'] > $lastVisit) {\n        // Mark the entry as "new"\n        $entry['is_new'] = true;\n    } else {\n        $entry['is_new'] = false;\n    }\n}