use Word2Number\Word2Number;\n\n$text = "iPhone has two hundred and thirty thousand seven hundred and eighty three apps";\npreg_match_all('/(two hundred and thirty thousand seven hundred and eighty three)/i', $text, $matches);\nforeach ($matches[1] as $match) {\n    $number = Word2Number::toNumber($match);\n    if ($number !== false) {\n        $text = str_replace($match, $number, $text);\n    }\n}\necho $text;