// Check if the request is a POST\nif ($_SERVER["REQUEST_METHOD"] == "POST") {\n    // Flex sends data in XML format, so we read from php://input\n    $xml = simplexml_load_string(file_get_contents('php://input'));\n    \n    // Ensure that the XML is not null before proceeding\n    if ($xml !== null) {\n        // Access the keyword from the XML structure\n        $keyword = (string)$xml->keyword;\n        \n        // Sanitize the input before echoing it\n        $sanitizedKeyword = filter_var($keyword, FILTER_SANITIZE_STRING);\n        \n        echo $sanitizedKeyword;\n    } else {\n        echo "Failed to parse XML";\n    }\n} else {\n    echo "Invalid request method";\n}