enum State: int {\n    case STATE_ONE = 1;\n    case STATE_TWO = 2;\n    case STATE_THREE = 3;\n}\n\n// Usage:\nswitch ($Object->getSomeFlag()) {\n    case State::STATE_ONE:\n        // do something\n        break;\n\n    case State::STATE_TWO:\n        // do something else\n        break;\n\n    case State::STATE_THREE:\n        // do yet something else\n        break;\n\n    default:\n        // Handle unexpected values\n        throw new InvalidArgumentException('Invalid state');\n}