function formatData($format, $variable) {\n    $formatParts = explode('-', $format);\n    $variable = strval($variable);\n    $result = '';\n\n    $offset = 0;\n    foreach ($formatParts as $part) {\n        $length = strlen($part);\n        $result .= substr($variable, $offset, $length);\n        $offset += $length;\n        if ($offset < strlen($variable)) {\n            $result .= '-';\n        }\n    }\n\n    return $result;\n}\n\n// Test the function\n$format = 'xxx-xxx';\n$variable = 123456;\necho formatData($format, $variable) . "\n"; // Output: 123-456\n\n$format = 'x-xxx';\n$variable = 1234;\necho formatData($format, $variable) . "\n"; // Output: 1-234\n\n$format = 'x-xx-xxx-xxxx';\n$variable = 1223334444;\necho formatData($format, $variable) . "\n"; // Output: 1-22-333-4444