$artist_name = $_POST['artist_name']; // Assuming this is the input from your search box\n\n$stmt = $mysqli->prepare("SELECT e.* \n                          FROM events e \n                          JOIN artist a ON e.artist_id = a.id \n                          WHERE a.name = ?");\n\n$stmt->bind_param("s", $artist_name);\n$stmt->execute();\n$result = $stmt->get_result();\n\nwhile ($row = $result->fetch_assoc()) {\n    // Display event information\n    echo $row['venue_name'] . '<br>';\n    echo $row['city'] . ', ' . $row['state'] . '<br>';\n    echo $row['date'] . ' ' . $row['time'] . '<br>';\n}