function svnMove($oldPath, $newPath) {\n    $command = "svn mv $oldPath $newPath";\n    exec($command, $output, $returnVar);\n    if ($returnVar !== 0) {\n        throw new Exception("SVN move failed: " . implode("\n", $output));\n    }\n}\n\nfunction svnDelete($path) {\n    $command = "svn rm $path";\n    exec($command, $output, $returnVar);\n    if ($returnVar !== 0) {\n        throw new Exception("SVN delete failed: " . implode("\n", $output));\n    }\n}\n\nfunction svnAdd($path) {\n    $command = "svn add $path";\n    exec($command, $output, $returnVar);\n    if ($returnVar !== 0) {\n        throw new Exception("SVN add failed: " . implode("\n", $output));\n    }\n}