function vote()\n{\n    $user_id = Auth::getUserId(); // Get the ID of the current user\n    $article_id = $this->id; // Get the ID of the current article\n\n    // Check if the user has already voted for the article\n    $query = "SELECT * FROM votes WHERE article_id = $article_id AND user_id = $user_id";\n    $result = parent::execSQL($query);\n    if ($result->num_rows > 0) {\n        // The user has already voted for the article\n        return false;\n    }\n\n    // Insert a new record into the votes table\n    $query = "INSERT INTO votes (article_id, user_id, vote_date) VALUES ($article_id, $user_id, NOW())";\n    parent::execSQL($query);\n\n    // Update the points of the article\n    $this->points++;\n    $this->save();\n\n    return true;\n}