$number = 12345;\n$formattedNumber = sprintf("%03d/%03d/%03d", \n    substr($number, 0, -4), \n    substr($number, -4, 3), \n    substr($number, -3)\n);\necho $formattedNumber; // outputs: 000/012/345\n\n$number = 9876543;\n$formattedNumber = sprintf("%03d/%03d/%03d", \n    substr($number, 0, -4), \n    substr($number, -4, 3), \n    substr($number, -3)\n);\necho $formattedNumber; // outputs: 009/876/543