use NlpTools\Tokenizers\ClassifierBasedTokenizer;\nuse NlpTools\Classifiers\NaiveBayesClassifier;\n\n// Initialize the tokenizer and classifier\n$tokenizer = new ClassifierBasedTokenizer();\n$classifier = new NaiveBayesClassifier();\n\n// Train the classifier with sample data\n$trainData = [\n    ['text' => 'I\'m looking to sell my house', 'category' => 'sell'],\n    ['text' => 'I\'m looking to get rid of my old house', 'category' => 'sell'],\n    ['text' => 'I\'m looking for a house', 'category' => 'buy'],\n    // ...\n];\n\nforeach ($trainData as $data) {\n    $tokens = $tokenizer->tokenize($data['text']);\n    $classifier->train($tokens, $data['category']);\n}\n\n// Classify new text\n$text = 'I\'m looking to buy a new car';\n$tokens = $tokenizer->tokenize($text);\n$category = $classifier->classify($tokens);\n\necho "Category: $category\n";