function time_since($event_time) {\n    $event_time = strtotime($event_time);\n    $current_time = time();\n    $time_diff = $current_time - $event_time;\n\n    $units = array(\n        "year" => 31536000,\n        "day" => 86400,\n        "hour" => 3600,\n        "minute" => 60,\n        "second" => 1\n    );\n\n    foreach ($units as $unit => $value) {\n        if ($time_diff >= $value) {\n            $time_diff /= $value;\n            return "It has been " . floor($time_diff) . " " . $unit . ($time_diff > 1 ? "s" : "");\n        }\n    }\n\n    return "It has been 0 seconds";\n}\n\n$event_time = "Sat Oct 25 21:55:29 +0000 2008";\nprint(time_since($event_time));