$xml  = file_get_contents('sample.xml');\n$json = file_get_contents('sample.js');\n\n$iters = 1000;\n\n// simplexml_load_string\n$start_xml = microtime(true);\n$mem_xml = memory_get_usage();\nfor ($i = 0; $i < $iters; ++$i) {\n    $obj = simplexml_load_string($xml, 'SimpleXMLElement', LIBXML_NOCDATA);\n}\n$end_xml = microtime(true);\n$mem_xml_end = memory_get_usage();\n\n// json_decode\n$start_json = microtime(true);\n$mem_json = memory_get_usage();\nfor ($i = 0; $i < $iters; ++$i) {\n    $obj = json_decode($json);\n}\n$end_json = microtime(true);\n$mem_json_end = memory_get_usage();\n\n// Print results\necho "XML elapsed: " . sprintf('%.4f', ($end_xml - $start_xml)) . "\n";\necho "XML memory usage: " . ($mem_xml_end - $mem_xml) . " bytes\n";\necho "JSON elapsed: " . sprintf('%.4f', ($end_json - $start_json)) . "\n";\necho "JSON memory usage: " . ($mem_json_end - $mem_json) . " bytes\n";