class myParent {\n    static $myVar = 'world';\n\n    static function hello() {\n        $calledClass = get_called_class();\n        echo $calledClass::$myVar;\n    }\n}\n\nclass myFirstChild extends myParent {\n    static $myVar = 'earth';\n}\n\nclass mySecondChild extends myParent {\n    static $myVar = 'planet';\n}\n\nmyFirstChild::hello(); // outputs "earth"\nmySecondChild::hello(); // outputs "planet"