$conn = oci_connect('username', 'password', 'host:port/service_name');\nif (!$conn) {\n    $e = oci_error();\n    print "Error occurred: " . $e['message'];\n    exit;\n}\n\n// Perform queries\n$stmt = oci_parse($conn, 'SELECT * FROM table_name');\noci_execute($stmt);\n\n// Fetch results\nwhile ($row = oci_fetch_array($stmt, OCI_ASSOC + OCI_RETURN_NULLS)) {\n    print_r($row);\n}\n\noci_close($conn);