class MyAnalyzer extends Zend_Search_Lucene_Analysis_Analyzer_Common_Utf8_CaseInsensitive\n{\n    public function __construct()\n    {\n        $this->addTokenFilter(new MyTokenFilter());\n    }\n}\n\nclass MyTokenFilter extends Zend_Search_Lucene_Analysis_TokenFilter\n{\n    public function normalize($token)\n    {\n        // Remove spaces and convert to lowercase\n        return strtolower(str_replace(' ', '', $token));\n    }\n}