function getYouTubeThumbnail($videoId, $size = 'big') {\n  if ($videoId === null) {\n    return "";\n  }\n  $size = ($size === null) ? "big" : $size;\n  $url = "https://img.youtube.com/vi/$videoId/";\n  switch ($size) {\n    case 'small':\n      $url .= "2.jpg";\n      break;\n    default:\n      $url .= "0.jpg";\n      break;\n  }\n  return $url;\n}\n\n$imgUrl_big = getYouTubeThumbnail("uVLQhRiEXZs");\n$imgUrl_small = getYouTubeThumbnail("uVLQhRiEXZs", 'small');