$modules['modulearr'] = $this->module_model->getModuleUser();\n\nforeach ($modules['modulearr'] as $module) {\n    $name = $module->mod_name;\n    $modelName = ucfirst($name) . '_model'; // assuming your model names are in PascalCase\n\n    // load the model dynamically\n    $this->load->model($modelName);\n\n    // get the data from the model\n    $data = $this->$modelName->get();\n\n    // load the view with the data\n    $this->load->view($name . '_view', $data);\n}