if($cmd=="submitinfo") {\n    if ($saleid == null) {\n       $statusQuery = "INSERT INTO STATUS VALUES (?, ?)";\n        if ($statusInfo = $con->prepare($statusQuery)) {\n                $statusInfo->bind_param("ss", $pk, $checkfield);\n                $statusInfo->execute();\n                $statusInfo->close();\n        } else {\n                print_r($con->error);\n        }\n    } else if ($saleid == $pk) {\n        $statusQuery = "UPDATE STATUS SET field = ? WHERE saleid = ?";\n        if ($statusInfo = $con->prepare($statusQuery)) {\n                $statusInfo->bind_param("ss", $checkfield, $pk);\n                $statusInfo->execute();\n                echo "Affected rows: " . $statusInfo->affected_rows . "\n";\n                $statusInfo->close();\n        } else {\n                print_r($con->error);\n        }\n    }\n}