$xml = simplexml_load_string('<time hour="18" minute="05" timezone="Eastern" utc-hour="-4" utc-minute="00" />');\n\n$hour = (string)$xml->attributes()->hour;\n$minute = (string)$xml->attributes()->minute;\n\n$time = strtotime($hour . ":" . $minute);\necho date("h:i A", $time); // outputs: 06:05 PM\n\n// if you want to include the timezone offset\n$utcHour = (string)$xml->attributes()->{'utc-hour'};\n$utcMinute = (string)$xml->attributes()->{'utc-minute'};\n$offset = $utcHour . ":" . $utcMinute;\necho date("h:i A O", $time); // outputs: 06:05 PM -0400