function collectLowestNumbers(array $numbers, int $count): array {\n    $minHeap = new SplMinHeap();\n    \n    foreach ($numbers as $number) {\n        $minHeap->insert($number);\n    }\n    \n    $result = [];\n    for ($i = 0; $i < $count; $i++) {\n        $result[] = $minHeap->extract();\n    }\n    \n    return $result;\n}\n\n$numbers = [12, 45, 7, 23, 56, 89, 34, 1, 6, 8, 9, 3, 5];\n$lowestNumbers = collectLowestNumbers($numbers, 10);\n\nprint_r($lowestNumbers);